// components/Driver.js
import React, { useState, useEffect } from 'react';
import './Driver.css'; // Create a separate CSS file for styles

function Driver() {
    const [drivers, setDrivers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortedDrivers, setSortedDrivers] = useState([]);

    // Fetch drivers data
    useEffect(() => {
        fetch('https://ergast.com/api/f1/2024/drivers.json')
            .then(response => response.json())
            .then(data => {
                setDrivers(data.MRData.DriverTable.Drivers);
                setSortedDrivers(data.MRData.DriverTable.Drivers);
            })
            .catch(error => console.error("Error fetching drivers:", error));
    }, []);

    // Handle search functionality
    const handleSearch = (event) => {
        const term = event.target.value.toLowerCase();
        setSearchTerm(term);
        setSortedDrivers(drivers.filter(driver => 
            driver.givenName.toLowerCase().includes(term) ||
            driver.familyName.toLowerCase().includes(term) ||
            driver.nationality.toLowerCase().includes(term)
        ));
    };

    return (
        <div className="driver">
            <header className="header">
                <h1>F1 Drivers 2024</h1>
                <input 
                    type="text" 
                    placeholder="Search drivers..." 
                    value={searchTerm}
                    onChange={handleSearch} 
                />
            </header>
            <div className="driver-list">
                {sortedDrivers.map(driver => (
                    <div key={driver.driverId} className="driver-card">
                        <h2>{`${driver.givenName} ${driver.familyName}`}</h2>
                        <p><strong>Nationality:</strong> {driver.nationality}</p>
                        <p><strong>Date of Birth:</strong> {new Date(driver.dateOfBirth).toLocaleDateString()}</p>
                        <p><strong>Permanent Number:</strong> {driver.permanentNumber}</p>
                        <a href={driver.url} target="_blank" rel="noopener noreferrer">More Info</a>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Driver;
