import React, { useEffect, useState } from 'react';

function Predict() {
    const [data, setData] = useState(null);
    const [constructors, setConstructors] = useState([]);
    const [drivers, setDrivers] = useState({});

    useEffect(() => {
        fetch('/predict/1')
            .then(res => res.json())
            .then(data => setData(data))
            .catch(error => console.error("Error fetching data:", error));
    }, []);


    useEffect(() => {
        const fetchConstructors = async () => {
            const response = await fetch('https://ergast.com/api/f1/2024/constructors/');
            const data = await response.json();
            console.log("Constructors data:", data); // Log the constructors data
            setConstructors(data.MRData.ConstructorTable.Constructors);
            
            for (const constructor of data.MRData.ConstructorTable.Constructors) {

                const driversResponse = await fetch(`https://ergast.com/api/f1/2024/constructors/${constructor.constructorId}/drivers`);
                const driversData = await driversResponse.json();
                console.log(`Drivers for ${constructor.name}:`, driversData); // Log the drivers data
                setDrivers(prev => ({
                    ...prev,
                    [constructor.constructorId]: driversData.MRData.DriverTable.Drivers,
                }));
            }
        };
    
        fetchConstructors();
    }, []);

    

    return (
        <div>
            <h1>
                {console.log(data)}
                {data ? data.message : "Loading..."}
            </h1>

            <h1>Choose a Driver</h1>
                {constructors.map(constructor => (
                    <div key={constructor.constructorId}>
                        <h2>{constructor.name}</h2>
                        <ul>
                            {drivers[constructor.constructorId]?.map(driver => (
                                <li key={driver.driverId}>{driver.givenName} {driver.familyName}</li>
                            ))}
                        </ul>
                    </div>
                ))}
        </div>
    );
}

export default Predict;
