// components/Home.js
import React, { useState, useEffect } from 'react';
import './Home.css';

function Home() {
    const [currentTime, setCurrentTime] = useState(0);
    const [calendar, setCalendar] = useState([]);

    useEffect(() => {
        fetch('/time')
            .then(res => res.json())
            .then(data => {
                setCurrentTime(data.time);
            });
    }, []);

    useEffect(() => {
        fetch('https://ergast.com/api/f1/current.json')
            .then((response) => response.json())
            .then((data) => setCalendar(data.MRData.RaceTable.Races))
            .catch((error) => console.error("Error fetching calendar:", error));
    }, []);

    return (
        <div className="home">
            <header className="header">
                <p>The current time is {currentTime}.</p>
                <img src="https://www.formula1.com/etc/designs/fom-website/images/f1_logo.svg" alt="F1 Logo" className="f1-logo" />
                <h1>2024 F1 Season Calendar</h1>
            </header>
            <div className="calendar">
                {calendar.map((race) => {
                    const isSprint = race.Sprint ? true : false; // Check if Sprint exists

                    return (
                        <div key={race.round} className={isSprint ? 'sprint-card' : 'race-card'}>
                            <h2>{race.raceName}</h2>
                            <p>{race.Circuit.circuitName}</p>
                            <p>{new Date(race.date).toLocaleDateString()}</p>
                            {race.Circuit.Location ? (
                                <p>{race.Circuit.Location.locality}, {race.Circuit.Location.country}</p>
                            ) : (
                                <p>Location data not available</p>
                            )}
                        </div>
                    );
                })}
            </div>
        </div>
    );
}

export default Home;
