import React, { useEffect, useState } from 'react';
import './Driver.css'; // Import your CSS file

function Predict() {
    const [data, setData] = useState(null);
    const [constructors, setConstructors] = useState([]);
    const [drivers, setDrivers] = useState({});
    const [loading, setLoading] = useState(true);
    const [selectedDrivers, setSelectedDrivers] = useState({});

    useEffect(() => {
        fetch('/predict/1')
            .then(res => res.json())
            .then(data => setData(data))
            .catch(error => console.error("Error fetching data:", error));
    }, []);

    useEffect(() => {
        const fetchConstructors = async () => {
            setLoading(true);
            try {
                const response = await fetch('https://ergast.com/api/f1/2024/constructors.json');
                const data = await response.json();
                setConstructors(data.MRData.ConstructorTable.Constructors);
                
                for (const constructor of data.MRData.ConstructorTable.Constructors) {
                    const driversResponse = await fetch(`https://ergast.com/api/f1/2024/constructors/${constructor.constructorId}/drivers.json`);
                    const driversData = await driversResponse.json();
                    setDrivers(prev => ({
                        ...prev,
                        [constructor.constructorId]: driversData.MRData.DriverTable.Drivers,
                    }));
                }
            } catch (error) {
                console.error("Error fetching constructors:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchConstructors();
    }, []);

    // Create a list of distinct driver-constructor pairs
    const driverConstructorPairs = [];

    constructors.forEach(constructor => {
        const constructorDrivers = drivers[constructor.constructorId] || [];
        constructorDrivers.forEach(driver => {
            driverConstructorPairs.push({
                driverId: driver.driverId,
                driverName: `${driver.givenName} ${driver.familyName}`,
                constructorName: constructor.name,
            });
        });
    });

    const handleSelectDriver = (driverId) => {
        setSelectedDrivers(prev => ({
            ...prev,
            [driverId]: !prev[driverId], // Toggle selection
        }));
    };

    return (
        <div className="driver-container">
            <h1>{data ? data.message : "Loading..."}</h1>

            {loading ? (
                <p>Loading constructors...</p>
            ) : (
                <div>
                    <h1>Choose a Driver</h1>
                    {driverConstructorPairs.length === 0 ? (
                        <p>No drivers found.</p>
                    ) : (
                        <div className="drivers">
                            {driverConstructorPairs.map(pair => (
                                <div
                                    key={pair.driverId}
                                    className={`driver-card ${selectedDrivers[pair.driverId] ? 'selected' : ''}`}
                                    onClick={() => handleSelectDriver(pair.driverId)}
                                >
                                    <div className="driver-name">{pair.driverName}</div>
                                    <div className="driver-details">
                                        {pair.constructorName}
                                        <input
                                            type="checkbox"
                                            checked={!!selectedDrivers[pair.driverId]}
                                            onChange={() => handleSelectDriver(pair.driverId)}
                                        />
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default Predict;
